import { getDay } from "date-fns";
import React, { useState } from "react";
import DatePicker from "react-datepicker";
import InputGroup from 'react-bootstrap/InputGroup'

import "react-datepicker/dist/react-datepicker.css";
import './Calender.css'

const Calender = () => {
  const [selectdDate, serselectdDate] = useState();
  // can use this function to check if the days are booked 
  const isWeekday = (date) => {
    const day = getDay(date);
    return day !== 0 && day !== 6;
  };
  console.log("Date", selectdDate)
  return (
    <div id='calender'>
      <h3> Please select a day </h3>
      <DatePicker selected={selectdDate} onChange={(date) => serselectdDate(date)}
        //format 
        dateFormat='yyyy/MM/dd'
        // limit startt day
        minDate={new Date()}
        //check weekend filter specific days
        filterDate={isWeekday}
        showMonthDropdown
        showYearDropdown
        scrollableYearDropdown

      />

      <div class='select-hour'>
        <h3> Please selcet a time slot </h3>
        <input type="radio" id="html" name="fav_language" value="HTML" />
        <label for="html">HTML</label><br />
        <input type="radio" id="css" name="fav_language" value="CSS" />
        <label for="css">CSS</label><br />
        <input type="radio" id="javascript" name="fav_language" value="JavaScript" />
        <label for="javascript">JavaScript</label>
      </div>
    </div>


  );
};

export default Calender;
