import React, { useState } from "react";
import DatePicker from "react-datepicker";
import moment from "moment";
import "react-datepicker/dist/react-datepicker.css";
import getDay from "date-fns/getDay";
import './Calender.css'

const Calender = () => {
  // define check-in and check-out state
  const [checkInDate, setCheckInDate] = useState(null);
  const [checkOutDate, setCheckOutDate] = useState(null);

  // define handler change function on check-in date
  const handleCheckInDate = (date) => {
    setCheckInDate(date);
    setCheckOutDate(null);
  };

  // define handler change function on check-out date
  const handleCheckOutDate = (date) => {
    setCheckOutDate(date);
  };

  //  check if the days are booked 
  // get all booked /date in event table
  const disabledDates = ["2021-11-21", "2021-12-23"];
  function disabledDate(current) {
    // Can not select Sundays and predefined days
    return disabledDates.find(date => date === moment(current).format("YYYY-MM-DD"));
  }

  return (
    <div id='calender'>
      <h3> Please select a day </h3>

      <div className="input-container">
        <div>
          <label>Start day</label>
          <DatePicker
            selected={checkInDate}
            minDate={new Date()}
            onChange={handleCheckInDate}
            disabledDate
            dateFormat="MMMM d, yyyy "
            placeholderText="Seelect start date"
            disabledDays={[
              new Date(2021, 12, 12),
              new Date(2021,10, 29),

            ]}

          />
        </div>
        <div>
          <label>End Day</label>
          <DatePicker
            selected={checkOutDate}
            minDate={checkInDate}
            onChange={handleCheckOutDate}
            dateFormat="MMMM d, yyyy "
            placeholderText="Seelect start date"
            disabledDays={[
              new Date(2021, 12, 12),
              new Date(2021,10, 29),

            ]}

          />
        </div>
      </div>


        {checkInDate && checkOutDate && (
          <div className="summary">
            <p>
              You book an event from from {moment(checkInDate).format("LL")} to{" "}
              {moment(checkOutDate).format("LL")}.
            </p>
          </div>
        )}


      <button style={{margin:'2em'}} class="btn btn-outline-light btn-lg px-5" type="submit"> Submit </button>
    </div>


  );
};

export default Calender;
