import { getDay } from "date-fns";
import React, { useState } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import './Calender.css'
import { Container } from "react-bootstrap";

const Calender = () => {
  const [startDate, setStartDate] = useState(new Date("2014/02/08"));
  const [endDate, setEndDate] = useState(new Date("2014/02/10"));
  const [selectHour, setSelectHour] = useState('');
  // can use this function to check if the days are booked 
  const isWeekday = (date) => {
    const day = getDay(date);
    return day !== 0 && day !== 6;
  };
  console.log("Date", startDate, endDate)
  return (
    <div id='calender'>
      <h3> Please select a day </h3>
      <>
      <DatePicker
        selected={startDate}
        onChange={(date) => setStartDate(date)}
        selectsStart
        startDate={startDate}
        endDate={endDate}
      />
      <DatePicker
        selected={endDate}
        onChange={(date) => setEndDate(date)}
        selectsEnd
        startDate={startDate}
        endDate={endDate}
        minDate={startDate}
      />
    </>

      <div class='select-hour'>
        <h4 style={{ color: "black" }}> Please selcet a time slot </h4>
        <Container>
          <input type="radio" id="time_slot-1" name="AM" value="Morning 9:00QM - 12:00AM" onChange={(e => setSelectHour(e.target.value))} />
          <label for="morning">Morning 9:00QM - 12:00AM</label><br />

          <input type="radio" id="time_slot-2" name="afternoon" value="Afternoon 13:00PM - 17:00PM" onChange={(e => setSelectHour(e.target.value))}/>
          <label for="afternoon">Afternoon 13:00PM - 17:00PM</label><br />

          <input type="radio" id="time_slot-3" name="evening" value="Evening 18:00Pm - 22:00 PM" onChange={(e => setSelectHour(e.target.value))}/>
          <label for="eveningt">Evening 18:00Pm - 22:00 PM</label>
        </Container>

        </div>

        <p style={{color:'white'}}>selected day/time {selectHour}</p>

        <button class="btn btn-outline-light btn-lg px-5" type="submit"> Submit </button>
      </div>


      );
};

      export default Calender;
