import { getDay } from "date-fns";
import React, { useState } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import './Calender.css'
import { Container, Row } from "react-bootstrap";

const Calender = () => {
  const [selectdDate, serselectdDate] = useState();
  const [selectHOur, setSelectHour] = useState();
  // can use this function to check if the days are booked 
  const isWeekday = (date) => {
    const day = getDay(date);
    return day !== 0 && day !== 6;
  };
  console.log("Date", selectdDate)
  return (
    <div id='calender'>
      <h3> Please select a day </h3>
      <DatePicker selected={selectdDate} onChange={(date) => serselectdDate(date)}
        //format 
        dateFormat='yyyy/MM/dd'
        // limit startt day
        minDate={new Date()}
        //check weekend filter specific days
        filterDate={isWeekday}
        showMonthDropdown
        showYearDropdown
        scrollableYearDropdown

      />

      <div class='select-hour'>
        <h4 style={{color:"black"}}> Please selcet a time slot </h4>
    <Container>
        <input type="radio" id="time_slot-1" name="AM" value="morning"/>
        <label for="morning">Morning 9:00QM - 12:00AM</label><br />

        <input type="radio" id="time_slot-2" name="afternoon" value="afternoon" />
        <label for="afternoon">Afternoon 13:00PM - 17:00PM</label><br />

        <input type="radio" id="time_slot-3" name="evening" value="evening" />
        <label for="eveningt">Evening 18:00Pm - 22:00 PM</label>
        </Container>

      </div>
      <button class="btn btn-outline-light btn-lg px-5" type="submit"> Submit </button>
    </div>


  );
};

export default Calender;
