import { getDay } from "date-fns";
import React, { useState } from "react";
import DatePicker from "react-datepicker";
import InputGroup from 'react-bootstrap/InputGroup'
import FormControl from "react-bootstrap/esm/FormCheckInput";
import "react-datepicker/dist/react-datepicker.css";
import './Calender.css'

const Calender = () => {
  const [selectdDate, serselectdDate] = useState();
  // can use this function to check if the days are booked 
  const isWeekday = (date) => {
    const day = getDay(date);
    return day !== 0 && day !== 6;
  };
  console.log("Date", selectdDate)
  return (
    <div id='calender'>
      <h3> Please select a day </h3>
      <DatePicker selected={selectdDate} onChange={(date) => serselectdDate(date)}
        //format 
        dateFormat='yyyy/MM/dd'
        // limit startt day
        minDate={new Date()}
        //check weekend filter specific days
        filterDate={isWeekday}
        showMonthDropdown
        showYearDropdown
        scrollableYearDropdown

      />
      <div class="form-check">
        <h3> Please selct  a time slot</h3>
        <input class="form-check-input" type="checkbox" value="" id="flexCheckDefault-1" />
        <label class="form-check-label" for="flexCheckDefault">
          Morning 9:00AM - 12:00
        </label><br/>
        <input class="form-check-input" type="checkbox" value="" id="flexCheckDefault-2" />
        <label class="form-check-label" for="flexCheckDefault">
          Afternoon 14:00PM - 17:00PM
        </label><br/>
        <input class="form-check-input" type="checkbox" value="" id="flexCheckDefault-3" />
        <label class="form-check-label" for="flexCheckDefault">
          Evening 18:00PM - 21:00PM
        </label><br/>
      </div>
    </div>


  );
};

export default Calender;
